configuration.config:
type Loaders is ("ROM", "RAM", "USER");

Loader : Loaders := external ("LOADER", "ROM");

type Boards is ("RM57-LAUNCHPAD");

Board : Boards := external ("BOARD", "RM57-LAUNCHPAD");

package Compiler is
   Common_Required_Switches := ("-mlittle-endian", "-mfpu=vfpv3-d16", "-mhard-float",
     "-mcpu=cortex-r4");

   for Leading_Required_Switches ("Ada") use
      Compiler'Leading_Required_Switches ("Ada") &
      Common_Required_Switches;

   for Leading_Required_Switches ("C") use
      Compiler'Leading_Required_Switches ("C") &
      Common_Required_Switches;

   for Leading_Required_Switches ("Asm_Cpp") use
      ("-c", "-mfpu=vfpv3-d16", "-mhard-float", "-mcpu=cortex-r4" );
end Compiler;

package Linker is
   for Required_Switches use Linker'Required_Switches &
     ("-L${RUNTIME_DIR(ada)}/adalib", "-lgnat") &
     Compiler.Common_Required_Switches &
     ("-nostdlib", "-lgcc");

   for Required_Switches use Linker'Required_Switches &
   ("-L", "${RUNTIME_DIR(ada)}/arch/", "-T", Board & "-memory-map.ld",
    "-T", "common-" & Loader & ".ld");
end Linker;

package Binder is
   for Required_Switches ("Ada") use Binder'Required_Switches ("Ada")
     & ("-nostdlib");
end Binder;
